export default {
  name: "Login",

  data() {
    return {
      phone: "",
      password: ""
    };
  },

  methods: {
    async userLogin() {
      try {
        const {
          phone,
          password
        } = this;

        if (phone && password) {
          await this.$store.dispatch("userLogin", {
            phone,
            password
          });
        } // 登录成功跳转到首页


        let toPath = this.$route.query.redirect || '/home';
        this.$router.push(toPath);
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
