import {reqCartList} from '@/api'
//规范:利用vuex存储数据
import { SET_USERID } from '@/utils/USER_ID';
const state ={
    //vuex仓库存储用户临时身份,vuex存储数据确实非持久化的，SET_USERID执行返回结果,可是本地存储获取的！！
    USER_ID: SET_USERID(),
    shopCartInfo: []
};
const mutations ={
    GETCARLIST(state,payload){
        state.shopCartInfo = payload
    }
};
const actions ={
  async  getCarList({commit}){
    let result =  await reqCartList()
    console.log(result);
    if(result.code == 200){
        commit('GETCARLIST',result.data)
    }
    }
};
const getters ={};

export default {
    state,
    mutations,
    actions,
    getters
}
