import {reqCategoryList,reqGetBannerList} from '@/api'

// home模块小仓库
const state = {
    categoryList:[],
};
const mutations = {
    CETGORYLIST(state, categoryList){
        state.categoryList = categoryList;
    }
};
const actions = {
    async categoryList({commit}){
        let result = await reqCategoryList();
        if(result.code == 200){
            commit('CETGORYLIST',result.data)
        }
    },


    getBannerList(){
        reqGetBannerList()
    }

};
const getters = {};

export default {
    state,
    mutations,
    actions,
    getters
}
