import { mapGetters } from "_vuex@3.6.2@vuex";
import ImageList from "./ImageList/ImageList";
import Zoom from "./Zoom/Zoom";
export default {
  name: "Detail",

  data() {
    return {
      skuNum: 1
    };
  },

  components: {
    ImageList,
    Zoom
  },
  computed: { ...mapGetters(["categoryView", "skuInfo", "spuSaleAttrList"]),

    skuImageList() {
      return this.skuInfo.skuImageList || [{}];
    }

  },

  mounted() {
    this.$store.dispatch("getGoodInfo", this.$route.params.skuId);
  },

  methods: {
    // 切换商品属性高亮
    changeChecked(saleAttrValue, arr) {
      arr.forEach(item => {
        item.isChecked = "0";
      });
      saleAttrValue.isChecked = "1";
    },

    handle(e) {
      //通过event事件对象获取用户输入内容[用户输入的内容一定是字符串类型的数据]
      let value = e.target.value * 1;
      this.skuNum = value ? value : 1; // //用户输入进来非法情况判断
      // if (isNaN(value) || value < 1) {
      //   this.skuNum = 1;
      // } else {
      //   //正常情况
      //   this.skuNum = parseInt(value);
      // }
    },

    // 添加购物车
    async addShopCart() {
      try {
        await this.$store.dispatch("addorUpdateShopCart", {
          skuId: this.$route.params.skuId,
          skuNum: this.skuNum
        }); // 浏览器存储功能，在路由跳转在之前，存储到浏览器中

        sessionStorage.setItem('SKUINFO', JSON.stringify(this.skuInfo));
        this.$router.push({
          path: "/addcartsuccess",
          query: {
            skuNum: this.skuNum
          }
        });
      } catch (error) {
        //失败干什么
        alert("加入购物车失败");
      }
    }

  }
};
