import Swiper from 'swiper';
import "swiper/css/swiper.min.css";
import { mapGetters } from "_vuex@3.6.2@vuex";
export default {
  name: "ImageList",
  props: ['skuImageList'],

  data() {
    return {
      //控制小图类名的索引值
      currentIndex: 0
    };
  },

  computed: { ...mapGetters(["skuInfo"])
  },
  watch: {
    skuInfo() {
      //保证数据发生修改,页面结构再次渲染完毕。在初始化Swiper实例
      this.$nextTick(() => {
        //初始化Swiper类的实例
        var mySwiper = new Swiper(this.$refs.cur, {
          //设置轮播图防线
          direction: "horizontal",
          // loop:true,
          // 如果需要前进后退按钮
          navigation: {
            nextEl: ".swiper-button-next",
            prevEl: ".swiper-button-prev"
          },
          //展示区域同时展示三张图片
          slidesPerView: 3
        });
      });
    }

  },
  methods: {
    getInfoIndex(index) {
      this.currentIndex = index;
      this.$bus.$emit('sendIndex', index);
    }

  }
};
