export default {
  name: "Pagination",
  props: ['pageNo', 'pageSize', 'total', 'continues'],
  computed: {
    // 总页数
    totalPage() {
      return Math.ceil(this.total / this.pageSize);
    },

    startAndEnd() {
      let start = 0,
          end = 0;
      const {
        pageNo,
        totalPage,
        continues
      } = this;

      if (totalPage < continues) {
        start = 1;
        end = totalPage;
      } else {
        start = pageNo - parseInt(continues / 2);
        end = pageNo + parseInt(continues / 2); // 约束头部

        if (start < 1) {
          start = 1;
          end = continues;
        } // 约束尾部


        if (end > totalPage) {
          end = totalPage;
          start = totalPage - continues + 1;
        }
      }

      return {
        start,
        end
      };
    }

  }
};
