// 登录与注册模块
import {reqGetCode} from '@/api'

const state = {};
const mutations = {
    GETCODE(state,code){
        state.code = code
    }
};
const actions = {
    // 返回验证码，正常情况是返回到手机
    async getCode({commit},phone){
       let result = await reqGetCode(phone)
       if(result.code == 200){
            commit('GETCODE',result.data)
            return 'ok'
       }else{
        return Promise.reject(new Error('faile'))
       }
    }
};
const getters = {};

export  default {
    state,
    mutations,
    actions,
    getters
}
